export const updateVotingStatus = async (item: any, status: any, options: any) => {
    const { log, database } = options;

    log.debug_info('Generating item params');
    const params = await getTaskStatusDynamoParams(item, status, options);
    log.debug_info('Params', params);

    log.debug_info('Getting item on database');
    const task = await database.update(params);
    log.debug_info('Returning info');

    return task;

}

const getTaskStatusDynamoParams = async (item: any, status: any, options: any) => {
    const { TENANT, STAGE } = options.config
    return {
        TableName: `${STAGE}_${TENANT}_planning_poker`,
        Key: item,
        UpdateExpression: 'set voting = :v',
        ExpressionAttributeValues: {
            ':v': status
        }
    }
}

import { dynamo_client } from '../repositories/DynamoDB'
import { logger } from '../utils/Logger'
import { config } from '../aws/config'
import { dynamo_mock } from '../mocks/DynamoDB'
import { mock_logger } from '../mocks/Logger'
import { config_mock } from '../mocks/config'
import { IGenerateOptions, IOptions } from '../interfaces/IGenerateOptions'

export const gen_options: IGenerateOptions = async (force = false) => {
    if (process.env.NODE_ENV === 'test' && force === false) {
        return {
            log: mock_logger(),
            database: await dynamo_mock(config_mock),
            config: config_mock,
        } as unknown as IOptions;
    }

    return {
        log: logger(process.env.DEBUG),
        database: await dynamo_client(config),
        config: config,
    };
}

import * as AWS from 'aws-sdk';
import { IConfig } from '../interfaces/IConfig';
import { IDynamoDB } from '../interfaces/IDynamoDB';
export const dynamo_client: IDynamoDB = async (config: IConfig, sdk: any = AWS) => {
    sdk.config.update({region: config.REGION});
    const dynamo = new sdk.DynamoDB.DocumentClient();

    return {
        fetch_all: async (params) => {
            return await dynamo.scan(params).promise();
        },
        fetch_one: async (params) => {
            return await dynamo.get(params).promise();
        },
        search: async (params) => {
            return await dynamo.query(params).promise();
        },
        insert: async (params) => {
            return await dynamo.put(params).promise();
        },
        update: async (params) => {
            return await dynamo.update(params).promise();
        },
    }
}

import { ILogLib, ILogLibFactory } from '../interfaces/ILogLib'

export const logger: ILogLibFactory = (debug: string = "false", terminal: Function = console.log): ILogLib => {
    const log = (...args: Array<unknown>) => {
        terminal(...args);
    }
    const debug_log = (...args: Array<unknown>) => {
        if(debug === "true") {
            terminal(...args);
        }
    }

    return {
        info: (...args: Array<unknown>) => {
            log('INFO::', ...args);
        },
        warning: (...args: Array<unknown>) => {
            log('WARNING::', ...args);
        },
        error: (...args: Array<unknown>) => {
            log('ERROR::', ...args);
        },
        debug_info: (...args: Array<unknown>) => {
            debug_log('INFO::', ...args);
        },
        debug_warning: (...args: Array<unknown>) => {
            debug_log('WARNING::', ...args);
        },
        debug_error: (...args: Array<unknown>) => {
            debug_log('ERROR::', ...args);
        },
    }
}

export const config = {
    STAGE: process.env.STAGE,
    TENANT: process.env.TENANT,
    REGION: process.env.REGION
}

import { dynamo_client } from '../repositories/DynamoDB'

class mock_dynamo_client {
    public scan() { return this }
    public get() { return this }
    public query() { return this }
    public put() { return this }
    public update() { return this }
    public promise() { return { "Items": [{},{}], "Item": {} } }
}

const mock_aws = {
    config: {
        update: () => {},
    },
    DynamoDB: {
        DocumentClient: () => { return new mock_dynamo_client() }
    }
}

export const dynamo_mock = async (config_mock: any) => {
    return await dynamo_client(config_mock, mock_aws)
}

import { logger } from '../utils/Logger';

export const mock_logger = () => {
    return logger();
}

export const config_mock = {
    STAGE: 'dev',
    TENANT: 'ic',
    REGION: 'us-east-2'
}

import { ICreateNewSprintTask, ICreateNewSprintTaskParams } from '../interfaces/ICreateNewSprintTask'

export const createNewSprintTask: ICreateNewSprintTask = async (task, options) => {
    const { database, log } = options;

    log.debug_info('Setting up database Params');
    const params = await createTaskDynamoParams(task, options);
    log.debug_info('Params', params);
    log.debug_info('Awaiting for insert');
    await database.insert(params);
    log.debug_info('Insert Done!!');
}

const createTaskDynamoParams: ICreateNewSprintTaskParams = async (task, options) => {
    const { TENANT, STAGE } = options.config
    return {
        TableName: `${STAGE}_${TENANT}_planning_poker`,
        Item: task
    }
}

export const getSprintsList = async (options: any) => {
    const { log, database } = options;

    log.debug_info('Generating item params');
    const params = await getTaskDynamoParams(options);
    log.debug_info('Params', params);

    log.debug_info('Getting item on database');
    const task = await database.fetch_all(params);
    log.debug_info('Returning info');

    let sprints = []

    for(const sprint of task.Items){
        sprints.push(sprint.sprint_id)
    };

    return sprints

}

const getTaskDynamoParams = async (options: any) => {
    const { TENANT, STAGE } = options.config
    return {
        TableName: `${STAGE}_${TENANT}_planning_poker`,
        ProjectionExpression: 'sprint_id'
    }
}

export const getSprintsTasksList = async (sprint_id: any, options: any) => {
    const { log, database } = options;

    log.debug_info('Generating item params');
    const params = await getTaskDynamoParams(sprint_id, options);
    log.debug_info('Params', params);

    log.debug_info('Getting item on database');
    const task = await database.search(params);
    log.debug_info('Returning info');
    log.debug_info('Return from Dynamo', task);

    let sprints = []

    for(const sprint of task.Items){
        sprints.push(sprint.task_id)
    };

    return sprints

}

const getTaskDynamoParams = async (sprint_id: any, options: any) => {
    const { TENANT, STAGE } = options.config
    return {
        TableName: `${STAGE}_${TENANT}_planning_poker`,
        KeyConditionExpression: 'sprint_id = :sprintId',
        ProjectionExpression: 'task_id',
        ExpressionAttributeValues: {
            ":sprintId": sprint_id
        }
    }
}

export const getTaskItem = async (item: any, options: any) => {
    const { log, database } = options;

    log.debug_info('Generating item params');
    const params = await getTaskDynamoParams(item, options);
    log.debug_info('Params', params);

    log.debug_info('Getting item on database');
    const task = await database.fetch_one(params);
    log.debug_info('Returning info');

    return task;

}

const getTaskDynamoParams = async (item: any, options: any) => {
    const { TENANT, STAGE } = options.config
    return {
        TableName: `${STAGE}_${TENANT}_planning_poker`,
        Key: item
    }
}

export const status_code = {
    OK: 200,
    INTERNAL_SERVER_ERROR: 500,
    CREATED: 201,
    BAD_REQUEST: 400
}

import { APIGatewayProxyHandler } from 'aws-lambda'
import { api_response } from '../utils/ApiResponse'
import { status_code } from '../constants/StatusCode'
import { gen_options } from '../utils/GenerateOptions'
import { updateVotingStatus } from '../business/ChangeTaskVotingStatus'
import { validate_joi } from '../utils/Validate'
import { changeTaskVotingStatusSchema } from '../schema/ChangeTaskVotingStatus'
import { IChangeTaskVotingStatus } from '../interfaces/IChangeTaskVotingStatus'

export const changeTaskVotingStatus: APIGatewayProxyHandler = async (event) => {
    const options = await gen_options()
    const { log } = options;

    try{
        log.info('Event', event);

        if (!event.pathParameters) {
            throw new Error('missing pathParameters');
        }

        const pathParameters = await validate_joi(changeTaskVotingStatusSchema, event.pathParameters) as unknown as IChangeTaskVotingStatus

        log.debug_info(pathParameters);
        const { sprint_id, task_id, status } = pathParameters
        const task = await updateVotingStatus({ sprint_id, task_id }, status, options);

        log.debug_info("Returning Success Message")
        return await api_response(status_code.OK, task);
    }catch(e){
        log.debug_error(e)
        return await api_response(status_code.BAD_REQUEST, {  });
    }
}

import { IAPIResponse } from '../interfaces/IAPIResponse'

export const api_response: IAPIResponse = async (status_code, body) => {
    return {
        statusCode: status_code,
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Access-Control-Allow-Methods": "*",
          "Access-Control-Allow-Headers": "*",
        },
        body: JSON.stringify(body),
    };
}

export const validate_joi = (schema: any, body: any): Promise<any> => {
    const result = schema.validate(body)
    if('error' in result) {
        throw new Error('erro de validao')
    }

    return result.value
}

